INSERT INTO animals (id, common_name, scientific_name, category, visibility_probability, short_description, location_description, map_url, photo_unlock_url, photo_lock_url) VALUES
(1, 'Isard', 'Rupicapra pyrenaica', 'Mamífer', 'Mitjana', 'L''isard és un ungulat elegant i àgil que habita els Pirineus. Es caracteritza per les seves banyes primes i corbades cap enrere, amb els mascles amb banyes més robustes. Durant l''hivern, el seu pelatge es torna més fosc i espès per protegir-se del fred intens. Són animals socials que viuen en ramats separats per sexes la major part de l''any.', 'Pirineus catalans, especialment al Parc Nacional d''Aigüestortes i Parc Natural de l''Alt Pirineu. Prefereixen zones rocoses i pendents pronunciades entre 1.500 i 3.000 metres d''altitud.', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d235867.163532817!2d0.9234430685716569!3d42.29103228557188!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a611400cd4e6fd%3A0xa03786b09d7b9a5c!2sPrepirineo!5e1!3m2!1ses!2ses!4v1763398', 'isard.jpg', 'mamifer_blocked.png'),
(2, 'Àliga Reial', 'Aquila chrysaetos', 'Ocell', 'Baixa', 'L''àliga reial és una de les aus rapinyaires més majestuoses dels nostres cels. Amb una envergadura alar que pot superar els 2 metres, és un caçador excepcional que s''alimenta principalment de conills, llebres i altres aus. Els parells són monògams i utilitzen el mateix niu any rere any, afegint-hi nous materials cada temporada de cria.', 'Serralades prepirinenques i zones muntanyenques de tot Catalunya. Nia en penya-segats inaccessibles i zones rocoses de les comarques de l''Alt Pirineu, Pallars Sobirà i Berguedà.', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d235867.163532817!2d0.9234430685716569!3d42.29103228557188!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a611400cd4e6fd%3A0xa03786b09d7b9a5c!2sPrepirineo!5e1!3m2!1ses!2ses!4v1763398', 'aliga.jpg', 'ocell_blocked.png'),
(3, 'Tortuga mediterrània', 'Testudo hermanni', 'Rèptil', 'Baixa', 'Aquesta tortuga terrestre es caracteritza pel seu closca bombada de colors groc i negre en patrons únics per a cada individu. És un rèptil de moviments lents que pot viure més de 80 anys. Durant els mesos freds, entra en hibernació sota terra, i a la primavera surt per alimentar-se de plantes, flors i fruites.', 'Zones de maquia mediterrània i boscos secs de les comarques litorals i prelitorals. Especialment present al Parc Natural del Montseny, Garraf i les Gavarres.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d1621655.7944284603!2d2.6436926826284797!3d40.8850554918016!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763399200963!5m2!1ses!2ses', 'tortuga.jpg', 'reptil_blocked.png'),
(4, 'Tritó Pirinenc', 'Calotriton asper', 'Amfibi', 'Molt Baixa', 'El gamarús és un tritó pirinenc adaptat a la vida en aigües fredes i oxigenades. El seu cos esvelt i la seva pell rugosa li permeten camuflar-se perfectament entre les roques dels rius. És una espècie endèmica dels Pirineus que passa la major part del seu temps sota l''aigua, on es nodreix d''insectes aquàtics i petits crustacis.', 'Rius i torrents d''aigües fredes i netes dels Pirineus catalans. Es troba especialment als rius de les valls d''Aran, Cardós i Farrera, on l''aigua manté temperatures baixes tot l''any.', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1124389.0737070823!2d-0.4564079423856335!3d42.15559228186768!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a8a48b202023d5%3A0x1711d86785a522d6!2sPirineos!5e1!3m2!1ses!2ses!4v176339', 'gamarus.jpg', 'amfibi_blocked.png'),
(5, 'Barb cua-roig', 'Barbus haasi', 'Peix', 'Mitjana', 'El barrallet és un ciprínid endèmic de les conques mediterrànies de la Península Ibèrica. Es caracteritza pel seu cos allargat i platejat, amb una línia lateral ben marcada. És un peix que prefereix les aigües clares amb corrent moderat i fons de grava, on es pot alimentar d''algues, larves d''insectes i petits invertebrats.', 'Rius de les conques mediterrànies catalanes, especialment als rius Ter, Fluvià i Francolí. Habita zones amb vegetació aquàtica abundant i corrents no massa forts.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d1621655.7944284603!2d2.6436926826284797!3d40.8850554918016!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763399200963!5m2!1ses!2ses', 'barrallet.jpg', 'peix_blocked.png'),
(6, 'Trencalós', 'Aquila fasciata', 'Ocell', 'Baixa', 'L''àliga perdiguer, també coneguda com a trencalòs, és una rapinyaire gran que viu en zones rocoses i boscos oberts. És una espècie amenaçada i protegida.', 'Habita en zones rocoses, cingles i penya-segats de muntanya mitjana. Es troba en àrees poc freqüentades per humans.', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d235867.163532817!2d0.9234430685716569!3d42.29103228557188!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a611400cd4e6fd%3A0xa03786b09d7b9a5c!2sPrepirineo!5e1!3m2!1ses!2ses!4v1763398', 'trencalos.png', 'ocell_blocked.png'),
(7, 'Llop', 'Canis lupus', 'Mamífer', 'Baixa', 'El llop ibèric és un carnívor de gran mida que viu en manades. És un animal esquiu i intel·ligent, clau per a l''equilibri dels ecosistemes.', 'Habita en zones forestals extenses i poc freqüentades, preferentement en àrees de muntanya amb cobertura vegetal densa.', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1124389.0737070823!2d-0.4564079423856335!3d42.15559228186768!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a8a48b202023d5%3A0x1711d86785a522d6!2sPirineos!5e1!3m2!1ses!2ses!4v176339', 'llop.png', 'mamifer_blocked.png'),
(8, 'Ós Bru', 'Ursus arctos', 'Mamífer', 'Baixa', 'L''os bru dels Pirineus és el mamífer més gran de la península Ibèrica. És una espècie protegida i en perill d''extinció que simbolitza la fauna més salvatge dels nostres boscos.', 'Habita en zones forestals extenses i remotes dels Pirineus, preferentment en zones amb abundància de baies i fruits secs.', 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1124389.0737070823!2d-0.4564079423856335!3d42.15559228186768!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12a8a48b202023d5%3A0x1711d86785a522d6!2sPirineos!5e1!3m2!1ses!2ses!4v176339', '1763380571545_1877495948.png', 'mamifer_blocked.png'),
(9, 'Mantis religiosa', 'Mantis religiosa', 'Insecte', 'Alta', 'La mantis religiosa és un insecte fascinant conegut per les seves potes davanteres que semblen en actitud de pregària. És un depredador excel·lent que es camufla perfectament entre la vegetació. Els mascles són més petits que les femelles, que sovint practiquen el canibalisme durant l''aparellament.', 'Es troba en zones amb vegetació abundant com prats, jardins, camps de conreu i zones arbustives de tot Catalunya. Prefereix zones assolellades amb herba alta i arbustos on pot caçar les seves preses.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', 'mantis.png', 'insecte_blocked.png'),
(10, 'Mussol comú', 'Athene noctua', 'Ocell', 'Mitjana', 'El mussol comú és una petita rapinyaire nocturna fácilment recognizable pels seus ulls grocs i plomatge bru amb taques blanques. És un ocell territorial que sovint es pot sentir durant la nit amb el seu característic crit "kiu-kiu". És un caçador eficient de rosegadors i insectes.', 'Habita zones obertes amb arbres dispersos, camps de conreu, oliverars i zones urbanes de tot Catalunya. Fa el niu en forats d''arbres, edificis vells o caixes niu. És comú a les comarques de Lleida, el Camp de Tarragona i les zones rurals de Barcelona.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763403582380_2126598465.png', 'ocell_blocked.png'),
(11, 'Ratpenat de ferradura gros', 'Rhinolophus ferrumequinum', 'Mamífer', 'Baixa', 'Aquest ratpenat és un dels més grans d''Europa, easily recognizable per la seva característica "ferradura" al nas que utilitza per a l''ecolocalització. És un caçador nocturn especialitzat en insectes grans com arnes i escarabats. Hiverna en coves i mines, i a l''estiu es refugia en edificis antics i arbres buits.', 'Habita zones amb cavitats naturals o artificials a tot Catalunya. Es troba en coves del Montsec, mines abandonades del Priorat, masies velles de les Guilleries i boscos madurs del Montseny. És especialment present a les zones càrstiques i en edificis històrics abandonats.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763404638068_1257334323.png', 'mamifer_blocked.png'),
(12, 'Serp verda i groga', 'Hierophis viridiflavus', 'Rèptil', 'Mitjana', 'Aquesta serp és fàcilment reconeixible pels seus colors verds i grocs en patrons característics. És una espècie àgil i ràpida, no verinosa, que pot arribar a mesurar fins a 2 metres. Els juvenils presenten un dibuix escalonat que dona nom a l''espècie, mentre els adults adopten colors més uniformes.', 'Habita zones assolellades amb pedres i vegetació de tot Catalunya, des del litoral fins als 2.000 metres d''altitud. Es troba en zones de matollar, marges de camins, roquissars i zones agrícoles. És especialment comuna al Montsec, els Ports de Tortosa-Beseit i les Guilleries.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', 'serp2.jpg', 'reptil_blocked.png'),
(13, 'Granota verda', 'Pelophylax perezi', 'Amfibi', 'Alta', 'La granota verda és un amfibi comú i fácilment recognizable pels seus tons verds i la línia dorsal clara. És una espècie principalmente aquàtica que es troba en estanys, rius i zones humides. És un depredador actiu que s''alimenta d''insectes, aranyes i petits invertebrats. Els mascles produeixen un cant característic durant l''època de reproducció.', 'Habita pràcticament totes les zones humides de Catalunya, des dels Aiguamolls de l''Empordà fins als estanys del Delta de l''Ebre. És comuna en estanys de parcs urbans, fonts de muntanya, rierols i zones de cultiu amb aigua. Es troba des del nivell del mar fins als 2.000 metres d''altitud als Pirineus.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763405999424_166529717.png', '1763405999422_2129205326.png'),
(14, 'Esquirol vermell', 'Sciurus vulgaris', 'Mamífer', 'Mitjana', 'L''esquirol vermell és un rosegador àgil i simpàtic, fácilment recognizable pel seu pelatge rogenc i la seva cua espessa i poblada. És un animal diürn que passa la major part del dia buscant aliment i sentint-se a les branques dels arbres. S''alimenta principalment de pinyes, nous, glans i fruits secs, que amagava per a l''hivern.', 'Habita boscos de coníferes i frondoses de tot Catalunya, des dels parcs urbans de Barcelona fins als boscos dels Pirineus. És especialment comú als boscos de pi roig del Montseny, les Gavarres i els Ports de Tortosa-Beseit. També es pot observar als parcs de la Ciutadella i el Laberint d''Horta a Barcelona.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763406234612_904445615.png', '1763406234610_827079002.jpg'),
(15, 'Papallona Reina', 'Papilio machaon', 'Insecte', 'Mitjana', 'La vibria és una de les papallones més grans i vistoses de Catalunya, es reconeix pels seus colors grocs i negres i les seves característiques cues als ales posteriors. És una voladora poderosa que es pot observar planant sobre prats i zones florides. Les erugues s''alimenten de plantes umbel·líferes com el fonoll.', 'Habita prats florits, zones herboses i marges de camins de tot Catalunya. És comuna als prats dels Pirineus, zones del Montseny i camps del Camp de Tarragona. Es pot observar volant des de primavera fins a tardor en zones amb abundància de flors.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763458620683_1910487716.png', '1763458620674_786892065.png'),
(16, 'Voltor comú', 'Gyps fulvus', 'Ocell', 'Mitjana', 'El voltor comú és una de les aus rapinyaires més grans i majestuoses dels cels catalans, amb una envergadura alar que pot superar els 2,5 metres. És un necròfag especialitzat que s''alimenta exclusivament de carronya, jugant un paper crucial en la neteja dels ecosistemes. Els voltors poden planejar durant hores senza batre les ales, aprofitant els corrents tèrmics.', 'Nia en penya-segats inaccessibles dels Pirineus i Prepirineu català. Es pot observar als congosts de Mont-rebei, les serres del Montsec, els Ports de Tortosa-Beseit i les zones muntanyenques de la Ribagorça. Es reuneixen en dormitoris comunals i cerquen aliment en zones d''alta muntanya i valls properes.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763459088809_1091714392.png', '1763459088806_1926392574.png'),
(17, 'Garsa', 'Pica pica', 'Ocell', 'Alta', 'La garsa (Pica pica) és un ocell molt intel·ligent i adaptable, reconeixable per son plomatge negre amb reflexos metàl·lics i el ventre blanc. És comuna en zones urbanes i rurals.', 'Present a tot Catalunya, des de zones urbanes fins camps de conreu. Freqüent en parcs, jardins, zones agrícoles i boscos oberts. S''ha adaptat excel·lentment als entorns humans.', 'https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d798884.4424193025!2d1.6463692442161728!3d41.850518340256556!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1slitoral%20catalunya!5e1!3m2!1ses!2ses!4v1763403289457!5m2!1ses!2ses', '1763460033149_1947599703.png', '1763460033147_2029102254.png');